DECLARE
  MAXVCRFID NUMBER;
  LASTVCRFID NUMBER;
  NEWVCRFID NUMBER;
BEGIN
  SELECT MAX(VALID_CLASS_REPORT_FREQ_ID)
  INTO MAXVCRFID
  FROM VALID_CLASS_REPORT_FREQ;
  SELECT LAST_NUMBER
  INTO LASTVCRFID
  FROM USER_SEQUENCES
  WHERE SEQUENCE_NAME = 'SEQ_VALID_CLASS_REPORT_FREQ';
  IF MAXVCRFID > LASTVCRFID + 1 THEN
    EXECUTE IMMEDIATE 'ALTER SEQUENCE SEQ_VALID_CLASS_REPORT_FREQ INCREMENT BY ' || TO_CHAR(MAXVCRFID - LASTVCRFID + 1);
    SELECT SEQ_VALID_CLASS_REPORT_FREQ.NEXTVAL
    INTO NEWVCRFID
    FROM DUAL;
    EXECUTE IMMEDIATE 'ALTER SEQUENCE SEQ_VALID_CLASS_REPORT_FREQ INCREMENT BY 1';
  END IF;
END;
