
  CREATE TABLE YNQ_TEMP 
   (	QUESTION_ID VARCHAR2(4 BYTE) NOT NULL ENABLE, 
	DESCRIPTION VARCHAR2(400 BYTE) NOT NULL ENABLE, 
	QUESTION_TYPE CHAR(1 BYTE) NOT NULL ENABLE, 
	NO_OF_ANSWERS NUMBER(2,0) NOT NULL ENABLE, 
	EXPLANATION_REQUIRED_FOR VARCHAR2(3 BYTE), 
	DATE_REQUIRED_FOR VARCHAR2(3 BYTE), 
	STATUS CHAR(1 BYTE) NOT NULL ENABLE, 
	EFFECTIVE_DATE DATE NOT NULL ENABLE, 
	UPDATE_TIMESTAMP DATE NOT NULL ENABLE, 
	UPDATE_USER VARCHAR2(8 BYTE) NOT NULL ENABLE, 
	GROUP_NAME VARCHAR2(150 BYTE)
   ) ;
 


update YNQ A set (QUESTION_ID,DESCRIPTION,QUESTION_TYPE,NO_OF_ANSWERS,EXPLANATION_REQUIRED_FOR,DATE_REQUIRED_FOR,STATUS,EFFECTIVE_DATE,GROUP_NAME) = 
		(select QUESTION_ID,DESCRIPTION,QUESTION_TYPE,NO_OF_ANSWERS,EXPLANATION_REQUIRED_FOR,DATE_REQUIRED_FOR,STATUS,EFFECTIVE_DATE,GROUP_NAME from OSP$YNQ_TEMP  
		where question_id=A.question_id);
		
