insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '3','E3','Research involving the use of educational tests (cognitive, diagnostic, aptitude, achievement), survey procedures, interview procedures, or observation of public behavior that is not exempt under paragraph (b)(2) of this section, if: (i) the human subjects are elected or appointed public officials or candidates for public office; or (ii) federal statute(s) require(s) without exception that the confidentiality of the personally identifiable information will be maintained throughout the research and thereafter.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','e2327d94-7b34-47f3-a80f-149236981aa2');
insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '5','E5','Research and demonstration projects which are conducted by or subject to the approval of department or agency heads, and which are designed to study, evaluate, or otherwise examine: (i) Public benefit or service programs; (ii) procedures for obtaining benefits or services under those programs; (iii) possible changes in or alternatives to those programs or procedures; or (iv) possible changes in methods or levels of payment for benefits or services under those programs.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','29a61b5f-b356-407d-a47e-815d47a2abf0');
insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '1','E1','Research conducted in established or commonly accepted educational settings, involving normal educational practices, such as (i) research on regular and special education instructional strategies, or (ii) research on the effectiveness of or the comparison among instructional techniques, curricula, or classroom management methods.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','03b43350-9bf4-44e5-8960-25e714cdb0e8');
insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '2','E2','Research involving the use of educational tests (cognitive, diagnostic, aptitude, achievement), survey procedures, interview procedures or observation of public behavior, unless: (i) information obtained is recorded in such a manner that human subjects can be identified, directly or through identifiers linked to the subjects; and (ii) any disclosure of the human subjects'' responses outside the research could reasonably place the subjects at risk of criminal or civil liability or be damaging to the subjects'' financial standing, employability, or reputation.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','9ea99c81-57a6-4287-8e46-e47e984f6f9a');
insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '6','E6','Taste and food quality evaluation and consumer acceptance studies, (i) if wholesome foods without additives are consumed or (ii) if a food is consumed that contains a food ingredient at or below the level and for a use found to be safe, or agricultural chemical or environmental contaminant at or below the level found to be safe, by the Food and Drug Administration or approved by the Environmental Protection Agency or the Food Safety and Inspection Service of the U.S. Department of Agriculture.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','d81ff192-0244-48b9-a2e6-696a8631720f');
insert into EXEMPTION_TYPE (EXEMPTION_TYPE_CODE,DESCRIPTION,DETAILED_DESCRIPTION,UPDATE_TIMESTAMP,UPDATE_USER,OBJ_ID) values ( '4','E4','Research involving the collection or study of existing data, documents, records, pathological specimens, or diagnostic specimens, if these sources are publicly available or if the information is recorded by the investigator in such a manner that subjects cannot be identified, directly or through identifiers linked to the subjects.',to_date('2010-01-28 12:00:00','YYYY-MM-DD HH24:MI:SS'),'admin','bf780727-5e02-4a36-b9bc-bb8aa07d307e');
